var WidgetApp = WidgetApp || {};

$(document).ready(function(){
  WidgetApp.routes.bind();
  WidgetApp.bindListeners();
  WidgetApp.emitOverlay();
  WidgetApp.emitInit();
})

var WidgetApp = WidgetApp || {};
(function(){
  var dispatchToParent = function(payload){
    window.parent.postMessage(
      JSON.stringify(payload),
      window.location.origin
    );
  }

  WidgetApp.emitOverlay = function(){
    var dataPayload = this.store.getMyEntities();
    var data = {doctors: dataPayload.doctors.length,
                scrips: dataPayload.scrips.length,
                facilities: dataPayload.facilities.length};
    var dispatch = document.createElement('div');
    riot.mountTo(dispatch, 'plan-overlay', data);
    dispatchToParent({overlayContent: dispatch.innerHTML});
  }

  WidgetApp.emitInit = function(){
    dispatchToParent({init: true});
  }
  
  WidgetApp.emitFactsAboutId = function(id){
    var data = this.store.rollUpCoverage(this.store.checkCoverage(id, this.store.getMyEntities()));
    if (data){
      data.planID = id;
      var dispatch = document.createElement('div');
      riot.mountTo(dispatch, 'plan-details', data);
      var planData = {};
      planData[id] = dispatch.innerHTML;
      dispatchToParent({planData: planData});
    }
  }
})();

var WidgetApp = WidgetApp || {};
(function(){
  WidgetApp.bindListeners = function(){
    window.addEventListener("message", function(event){
      // Only accept same-origin messages for now
      var payload = extractPayload(event);
      if (payload.planID){
        WidgetApp.emitFactsAboutId(payload.planID)
      } else if (payload.routeTo){
        WidgetApp.routes.handleRouteTo(payload.routeTo);
      }
    }, false);
  }
})();

var WidgetApp = WidgetApp || {};
(function(){

  var routes = {
    plans: {'id': 'plan-page', 'default': 'plans-page'},
    doctors: 'doctors-page',
    scrips: 'scrips-page',
    facilities: 'facilities-page',
    'default': 'home-page',
    'home': 'home-page'
  }
  
  var routeFunction = function(collection, id, action){
    var baseRoute = routes[collection] || routes['default'];
    if (id && baseRoute.id) {
      var ids = id.toString().split(',');
      if (ids.length == 1){
        riot.mount('body', baseRoute.id, {id: id});
      } else {
        riot.mount('body', baseRoute['default'], {ids: ids});
      }
    } else {
      riot.mount('body', baseRoute);
    }
  };
  
  WidgetApp.routes = {}

  WidgetApp.routes.handleRouteTo = function(data){
    if (data.planID){
      riot.route('plans/'+data.planID);
    } else if (data.section){
      riot.route(''+data.section);
    } else {
      riot.route('home');
    }
  }
  
  WidgetApp.routes.bind = function(){
    riot.route.exec(routeFunction);
    riot.route(routeFunction);
  }
})();


var WidgetApp = WidgetApp || {};
(function(){
  var store = {};
  store.checkCoverage = function(planId, data){
    var output = {};
    // Consider it covered if the planId and the entity id share any chars
    var planChars = planId.toString().split('');
    $.each(data, function(type, list){
      var typeList = output[type] = {};
      $.each(list, function(i, id){
        var found = false;
        var idChars = id.toString().split('');
        for (var i=0; i<idChars.length; i++){
          if (planChars.indexOf(idChars[i]) > -1) found = true;
        }
        typeList[id] = found;
      });
        
    });
    return output;
  }
  
  var dummyData = {
    doctors: [1,2,3,9,8,6],
    scrips: [45, 66, 89],
    facilities: [345]
  };

  store.rollUpCoverage = function(coverageData){
    var output = {}
    $.each(coverageData, function(type, data){
      var coveredCount = 0,
          uncoveredCount = 0;
      $.each(data, function(id, value){
        if (value){
          coveredCount++;
        } else {
          uncoveredCount++;
        }
      });
      output[type] = {covered: coveredCount, uncovered: uncoveredCount, total: coveredCount+uncoveredCount};
    });
    return output;
  }
  
  store.getMyEntities = function(){
    return dummyData;
  }
  WidgetApp.store = store;

})();

